const sync = (data) => localStorage.setItem(PERSIST_KEY, JSON.stringify(data));

export const useSheetStore = create((set, get) => ({
  topics: JSON.parse(localStorage.getItem(PERSIST_KEY)) || [],
  searchQuery: "", 

  setSearchQuery: (query) => set({ searchQuery: query }),

  initSheet: () => {
    if (get().topics.length > 0) return;
    
    const initial = STRIVER_MASTER_DATA.map(topic => ({
      id: uuid(),
      title: topic.title,
      completed: false,
      subtopics: topic.subtopics.map(sub => ({
        id: uuid(),
        title: sub.title,
        completed: false,
        questions: sub.questions.map(q => ({
          id: uuid(),
          title: q.title,
          url: q.url,
          companies: q.companies,
          difficulty: q.difficulty,
          completed: false
        }))
      }))
    }));

    set({ topics: initial });
    sync(initial);
  },

  addTopic: (title) => set(s => {
    const next = [...s.topics, { id: uuid(), title, completed: false, subtopics: [] }];
    sync(next); return { topics: next };
  }),
  editTopic: (id, title) => set(s => {
    const next = s.topics.map(t => t.id === id ? { ...t, title } : t);
    sync(next); return { topics: next };
  }),
  deleteTopic: (id) => set(s => {
    const next = s.topics.filter(t => t.id !== id);
    sync(next); return { topics: next };
  }),
  addSub: (tId, title) => set(s => {
    const next = s.topics.map(t => t.id === tId ? { ...t, subtopics: [...t.subtopics, { id: uuid(), title, completed: false, questions: [] }] } : t);
    sync(next); return { topics: next };
  }),
  addQ: (tId, sId, title, url) => set(s => {
    const next = s.topics.map(t => t.id === tId ? { ...t, subtopics: t.subtopics.map(st => st.id === sId ? { ...st, questions: [...st.questions, { id: uuid(), title, url: url || '#', companies: [], difficulty: "Easy", completed: false }] } : st) } : t);
    sync(next); return { topics: next };
  }),
  deleteQ: (tId, sId, qId) => set(s => {
    const next = s.topics.map(t => t.id === tId ? { ...t, subtopics: t.subtopics.map(st => st.id === sId ? { ...st, questions: st.questions.filter(q => q.id !== qId) } : st) } : t);
    sync(next); return { topics: next };
  }),
  toggleQ: (tId, sId, qId) => set(s => {
    const next = s.topics.map(t => {
      if (t.id !== tId) return t;
      const subs = t.subtopics.map(st => {
        if (st.id !== sId) return st;
        const qs = st.questions.map(q => q.id === qId ? { ...q, completed: !q.completed } : q);
        return { ...st, questions: qs, completed: qs.length > 0 && qs.every(q => q.completed) };
      });
      return { ...t, subtopics: subs, completed: subs.length > 0 && subs.every(s => s.completed) };
    });
    sync(next); return { topics: next };
  }),
  reorder: (act, ovr) => set(s => {
    const oldIdx = s.topics.findIndex(t => t.id === act);
    const newIdx = s.topics.findIndex(t => t.id === ovr);
    const next = arrayMove(s.topics, oldIdx, newIdx);
    sync(next); return { topics: next };
  })
}));